import React, { Component } from "react";
import dogcard from "./components/dogcards";
import Wrapper from "./components/wrapper";
import Nav from "./components/nav";
import Hero from "./components/hero";
import Footer from "./components/footer";
import dog from "./dogs.json";

class App extends Component {
  state = {
    score: 0,
    topscore: 0,
    msg: "Click an image!",
    dog,
    chosenDogID: [],
    textColor: "black",
  };
  sortdogs = (id) => {
    var chosenDogID = this.state.chosenDogID;
    if (chosenDogID.includes(id)) {
      this.setState({
        score: 0,
        msg: "Nope!",
        chosenDogID: [],
        textColor: "red",
      });
      return;
    } else {
      chosenDogID.push(id);
      var topscore = this.state.topscore;
      var score = this.state.score;
      if (chosenDogID.includes(id) && topscore === score) {
        this.setState({ topscore: topscore + 1, score: score + 1 });
      }
      if (score === 12) {
        this.setState({ msg: "Winner", chosenDogID: [], textColor: "green" });
        console.log("Winner");
        return;
      }

      var newDogs = dog.sort((a, b) => Math.random() - 0.5);
      this.setState({
        score: score + 1,
        msg: "Correct!",
        animals: newDogs,
        chosenDogID,
        textColor: "greem",
      });
    }
  };

  render() {
    return (
      <div>
        <Nav
          score={this.state.score}
          topscore={this.state.topscore}
          msg={this.state.msg}
          textColor={this.state.textColor}
        />

        <Hero />

        <Wrapper>
          {this.state.dog.map((dog) => (
            <dogcard
              sortdogs={this.sortdogs}
              id={dog.id}
              key={dog.id}
              image={dog.image}
              alt={dog.name}
            />
          ))}
        </Wrapper>
        <Footer />
      </div>
    );
  }
}
export default App;
